import React, { useState, useEffect } from 'react';
import { Route, Routes, Link } from 'react-router-dom';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Boards from './pages/Boards';

function App() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
      <div className="flex justify-between items-center p-4">
        <nav className="space-x-4">
          <Link to="/login" className="text-blue-500 dark:text-blue-300">Login</Link>
          <Link to="/signup" className="text-blue-500 dark:text-blue-300">Signup</Link>
          <Link to="/boards" className="text-blue-500 dark:text-blue-300">Boards</Link>
        </nav>
        <button
          className="bg-gray-300 dark:bg-gray-700 text-black dark:text-white px-4 py-2 rounded"
          onClick={() => setDarkMode(!darkMode)}
        >
          Toggle Dark Mode
        </button>
      </div>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/boards" element={<Boards />} />
      </Routes>
    </div>
  );
}

export default App;
